import React, { Component } from 'react';
import BookCard from './BookCard';
import PropTypes from 'prop-types';
import * as BooksAPI from './BooksAPI'
import { Link } from 'react-router-dom';


class AddBook extends React.Component {
  static propTypes = {
      books: PropTypes.array.isRequired,
      moveToShelf: PropTypes.func.isRequired,
  }

  state = {
  query: '',
  books: []
}

editQuery = (query)=>{
  this.setState({
      query: query.trim()
  })
  this.updateBooks(query)
}

updateBooks = (category) => {
    category ?
      BooksAPI.search(category).then((books) => books && books.error ?
        this.setState({ category, error: books.error, books: [] }) :
        this.setState({ category, books, error: '' })) :
      this.setState({ category: '', books: [], error: '' });

 }
    render() {
      const { books, moveToShelf } = this.props

        return (
            <div className="search-books">
                <div className="search-books-bar">
                  <button className="close-search" onClick={() => this.setState({ showSearchPage: false })}>Close</button>
                    <div className="search-books-input-wrapper">

                        <input
                          type="text"
                          placeholder="Search by title or author"
                          onChange={(e) => this.editQuery(e.target.value)}
                        />

                    </div>
                </div>
                <div className="search-books-results">
                  <ol className="books-grid">
                      {books
                          .map((book) => (
                              <li key={book.id}>
                                  <BookCard
                                      bookCard={book}
                                      moveToShelf={moveToShelf} />
                              </li>
                          ))}
                  </ol>
                </div>
            </div>
        )
    }
}

export default AddBook
