import React, { Component } from 'react';
import BookCard from './BookCard';
import PropTypes from 'prop-types';
import * as BooksAPI from './BooksAPI'
import { Link } from 'react-router-dom';


class AddBook extends React.Component {
  static propTypes = {
      moveToShelf: PropTypes.func.isRequired,
  }

  state = {
  query: '',
  books: []
}

updateSearch = (query) => {
  this.setState({ query })

  if (this.state.query) {
    BooksAPI.search(query)
    .then((queryBooks) => {
      if (queryBooks instanceof Array) {
        queryBooks.forEach( queryBook => {
          queryBook.shelf = 'none'
          this.props.books.map( (bookInLibrary) => {
            if (bookInLibrary.id === queryBook.id) {
              queryBook.shelf = bookInLibrary.shelf
            }
            return queryBook
          })
        })
        this.setState({ showingBooks: queryBooks})
      } else {
        this.setState({ showingBooks: []})
      }
    })
    .catch( error => console.log(error) )
  } else {
    this.setState({ showingBooks: []})
  }
}
    render() {
      const { books, moveToShelf } = this.props

        return (
            <div className="search-books">
                <div className="search-books-bar">
                  <button className="close-search" onClick={() => this.setState({ showSearchPage: false })}>Close</button>
                    <div className="search-books-input-wrapper">

                        <input
                          type="text"
                          placeholder="Search by title or author"
                          onChange={(e) => this.updateSearch(e.target.value)}
                        />

                    </div>
                </div>
                <div className="search-books-results">
                  <ol className="books-grid">
                      {books
                          .map((book) => (
                              <li key={book.id}>
                                  <BookCard
                                      bookCard={book}
                                      moveToShelf={moveToShelf} />
                              </li>
                          ))}
                  </ol>
                </div>
            </div>
        )
    }
}

export default AddBook
